// 1) Gmail ë…¸ë“œ ì›ë³¸
const emailData = $input.item.json;

/*â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€  2) ìœ í‹¸  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€*/
function cleanText(content) {
  if (!content) return '';
  return content
    .replace(/&#x([0-9A-F]+);/gi, (_, h) => String.fromCharCode(parseInt(h, 16)))
    .replace(/&#([0-9]+);/g,        (_, d) => String.fromCharCode(parseInt(d, 10)))
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/&apos;/g, "'")
    .replace(/<[^>]*>/g, '')
    .replace(/\s+/g, ' ')
    .trim();
}

function parseEmailInfo(obj) {
  if (!obj?.value?.[0]) return { email: '', name: '', domain: '' };
  const { address: email = '', name = '' } = obj.value[0];
  return { email, name: name || email, domain: email.split('@')[1] || '' };
}

/*â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€  3) ICS íŒŒì„œ  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€*/
function parseICS(rawIcs) {
  const lines = rawIcs.split(/\r?\n/);

  const res = { date: '', startTime: '', endTime: '', participants: [] };
  let dtStart = '', dtEnd = '';

  for (const ln of lines) {
    if (ln.startsWith('DTSTART')) dtStart = ln.split(':')[1].trim();
    else if (ln.startsWith('DTEND')) dtEnd = ln.split(':')[1].trim();
    else if (ln.startsWith('ATTENDEE')) {
      const m = ln.match(/mailto:([^;]+)/i);
      if (m) res.participants.push(m[1].toLowerCase());
    }
  }

  const toLocal = dt => {
    if (!dt) return { d: '', t: '' };
    const y = dt.slice(0, 4), m = dt.slice(4, 6), d = dt.slice(6, 8);
    const h = dt.slice(9, 11), min = dt.slice(11, 13);
    const local = new Date(Date.parse(`${y}-${m}-${d}T${h}:${min}:00Z`));
    return {
      d: `${local.getFullYear()}-${String(local.getMonth() + 1).padStart(2, '0')}-${String(local.getDate()).padStart(2, '0')}`,
      t: `${String(local.getHours()).padStart(2, '0')}:${String(local.getMinutes()).padStart(2, '0')}`
    };
  };

  if (dtStart) {
    const { d, t } = toLocal(dtStart);
    res.date = d; res.startTime = t;
  }
  if (dtEnd) res.endTime = toLocal(dtEnd).t;

  res.participants = [...new Set(res.participants)];
  return res;
}

/*â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€  4) ë³¸ë¬¸(ì •ì œë§Œ)  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€*/
const bodyRaw = emailData.text || emailData.textAsHtml || emailData.html || '';
const body    = cleanText(bodyRaw);

/*â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€  5) ICS ì²¨ë¶€ í™•ì¸  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€*/
let isoInfo  = { date: '', startTime: '', endTime: '', participants: [] };
let isoFound = false;

if (Array.isArray(emailData.attachments)) {
  const ics = emailData.attachments.find(att =>
    (att.mimeType && att.mimeType.includes('calendar')) ||
    (att.fileName && att.fileName.toLowerCase().endsWith('.ics'))
  );
  if (ics?.content) {
    isoInfo = parseICS(Buffer.from(ics.content, 'base64').toString('utf8'));
    if (isoInfo.date || isoInfo.startTime || isoInfo.endTime || isoInfo.participants.length)
      isoFound = true;
  }
}

/*â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€  6) ë°œì‹ Â·ìˆ˜ì‹   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€*/
const fromInfo = parseEmailInfo(emailData.from);
const toInfo   = parseEmailInfo(emailData.to);

/*â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€  7) ë‹¨ì¼ ë˜í•‘ ê°ì²´ ë°˜í™˜  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€*/
return {
  "ë°ì´í„°": {
    "ë©”ì‹œì§€ID" : emailData.messageId || emailData.id || '',
    "ìŠ¤ë ˆë“œID" : emailData.threadId  || '',

    "ì œëª©"     : cleanText(emailData.subject || ''),
    "ì •ì œì œëª©" : cleanText(emailData.subject || '').replace(/^(RE:|FW:|Re:|Fwd:|ë‹µë³€:|ì „ë‹¬:)\s*/i, ''),

    "ë°œì‹ ì´ë©”ì¼" : fromInfo.email,
    "ë°œì‹ ìì´ë¦„" : fromInfo.name,
    "ìˆ˜ì‹ ì´ë©”ì¼" : toInfo.email,
    "ìˆ˜ì‹ ìì´ë¦„" : toInfo.name,

    "ë³¸ë¬¸"     : body,
    "ë³¸ë¬¸ê¸¸ì´" : body.length,

    "ISOìˆìŒ"  : isoFound,
    "ISOì •ë³´"  : {
      "ë‚ ì§œ"     : isoInfo.date,
      "ì‹œì‘ì‹œê°„" : isoInfo.startTime,
      "ì¢…ë£Œì‹œê°„" : isoInfo.endTime,
      "ì°¸ì„ì"   : isoInfo.participants
    },

    "ë‚ ì§œISO"  : (() => {
      try { return new Date(emailData.date).toISOString(); }
      catch { return new Date().toISOString(); }
    })(),

    "ì²˜ë¦¬ì‹œê°" : new Date().toISOString()
  }
};

